CREATE DATABASE diabetes_tip_2;
USE diabetes_tip_2;

CREATE TABLE datos_diabetes (
    alcaldia_id VARCHAR(4) NOT NULL,
    periodo YEAR NOT NULL,
    grupo_edad VARCHAR(10) NOT NULL,
    poblacion_total INT NOT NULL,
    poblacion_hombres INT NOT NULL,
    poblacion_mujeres INT NOT NULL,
    casos_diabetes INT NOT NULL,
    costo_alopata_anual DECIMAL(10,2) NOT NULL,
    costo_alopata_mensual DECIMAL(10,2) NOT NULL,
    costo_homeo_anual DECIMAL(10,2) NOT NULL,
    costo_homeo_mensual DECIMAL(10,2) NOT NULL,
    costo_total_estimado_alopata DECIMAL(12,2) NOT NULL,
    costo_total_estimado_homeo DECIMAL(12,2) NOT NULL
);





SELECT * FROM diabetes_tip_2.datos_diabetes;